<script>

    $(function () {
        Ziliao();
    });
    function Ziliao() {

        $.ajax({
            url: 'http://localhost:50222/api/UserInfoAdd/UserMessageFill?id=' + getCookie(""NameId""),
            type: 'get',
            contentType: ""application/json"",
            success: function (d) {
                $(""#Uname"").val(d.uname);
                $(""#RealName"").val(d.realName);
                $(""#Pass"").val(d.pass);
                $(""#Phone"").val(d.phone);
                $(""#Email"").val(d.email);
            }
        })
    }

    function getCookie(NameId) {
        if (document.cookie.length > 0) {
            c_start = document.cookie.indexOf(NameId + ""="")
            if (c_start != -1) {
                c_start = c_start + NameId.length + 1
                c_end = document.cookie.indexOf("";"", c_start)
                if (c_end == -1) c_end = document.cookie.length
                return unescape(document.cookie.substring(c_start, c_end))
            }
    ");
            WriteLiteral(@"    }
        return """"
    }

    function UserMessageUpt() {
        //md5加密
        var Pass = $(""#NewPass"").val();
        var p = hex_md5(Pass);
        if (Pass == """") {
            alert('请输入新的密码');
            return;
        }
        var obj = {};
        obj.uname = $(""#Uname"").val();
        obj.realName = $(""#RealName"").val();
        obj.pass = p;
        obj.email = $(""#Email"").val();
        obj.phone = $(""#Phone"").val();

        obj.id = parseInt(getCookie(""NameId""));

        $.ajax({
            url: 'http://localhost:50222/api/UserInfoAdd/UserMessageUpt',
            type: 'Post',
            data: JSON.stringify(obj),
            contentType: ""application/json"",
            success: function (d) {
                if (d > 0) {
                    alert('保存成功');
                }
                else {
                    alert('保存失败');
                }
            }
        })
    }
    function Fan() {
        location.href = '/Statisticss/Homepage'");
            WriteLiteral(@"
    }



    //二维码生成
    window.onload = function () {

        var qrcode = new QRCode(document.getElementById(""qrcode""), {

            width: 200,//设置宽高

            height: 200

        });

        qrcode.makeCode(""http://www.baidu.com"");

        document.getElementById(""send"").onclick = function () {
            var params = serializeForm('get');

            qrcode.makeCode(params);

        }

    }


</script>

<style>

    #qrcode {
        text-align: center;
        display: table-cell;
        width: 96px;
        height: 96px;
        vertical-align: middle;
        position: relative;
    }
</style>


